import{_ as a,o as i,c as t,ag as n}from"./chunks/framework.BSbZkL3e.js";const u=JSON.parse('{"title":"HappyView","description":"","frontmatter":{},"headers":[],"relativePath":"happyview/README.md","filePath":"happyview/README.md"}'),s={name:"happyview/README.md"};function o(r,e,l,p,c,d){return i(),t("div",null,[...e[0]||(e[0]=[n(`<h1 id="happyview" tabindex="-1">HappyView <a class="header-anchor" href="#happyview" aria-label="Permalink to &quot;HappyView&quot;">​</a></h1><p>HappyView is a lexicon-driven ATProto AppView. Upload lexicon definitions at runtime and HappyView dynamically generates XRPC query and procedure endpoints, indexes records from the network via Jetstream, and proxies writes to users&#39; PDSes --- no restart required.</p><h2 id="how-it-fits-together" tabindex="-1">How it fits together <a class="header-anchor" href="#how-it-fits-together" aria-label="Permalink to &quot;How it fits together&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Jetstream ──&gt; HappyView ──&gt; PostgreSQL</span></span>
<span class="line"><span>                 │</span></span>
<span class="line"><span>     ┌───────────┼───────────┐</span></span>
<span class="line"><span>     │           │           │</span></span>
<span class="line"><span>   Clients     AIP        PDSes</span></span>
<span class="line"><span>              (OAuth)   (user repos)</span></span></code></pre></div><ul><li><strong>Jetstream</strong> pushes real-time record events. HappyView subscribes to the collections defined by uploaded lexicons and indexes records into Postgres.</li><li><strong>AIP</strong> (ATProto Identity Provider) handles OAuth 2.1 with PKCE. HappyView validates tokens by calling AIP&#39;s <code>/oauth/userinfo</code> endpoint.</li><li><strong>PDSes</strong> store user data. HappyView proxies writes and blob uploads to each user&#39;s PDS using DPoP-authenticated requests.</li><li><strong>Clients</strong> talk to HappyView&#39;s XRPC and admin APIs. Any ATProto-compatible client can connect.</li></ul><h2 id="docs" tabindex="-1">Docs <a class="header-anchor" href="#docs" aria-label="Permalink to &quot;Docs&quot;">​</a></h2><ul><li><a href="./quickstart.html">Quickstart</a> - get a local instance running</li><li><a href="./configuration.html">Configuration</a> - environment variables reference</li><li><a href="./deployment.html">Deployment</a> - Docker, production, TLS</li><li><a href="./lexicons.html">Lexicons</a> - uploading and managing lexicon definitions</li><li><a href="./network-lexicons.html">Network Lexicons</a> - loading lexicons from the ATProto network</li><li><a href="./backfill.html">Backfill</a> - bulk-indexing historical records</li><li><a href="./xrpc-api.html">XRPC API</a> - query and procedure endpoints</li><li><a href="./admin-api.html">Admin API</a> - manage lexicons, backfills, and admins</li><li><a href="./architecture.html">Architecture</a> - internals for contributors</li></ul>`,7)])])}const m=a(s,[["render",o]]);export{u as __pageData,m as default};
